INSERT INTO University (UniversityId, Address, Description, NumberOfStudents) VALUES ('b0cae1190b03406980d639c5447fcb21', "4000 Central Florida Blvd, Orlando, FL 32816", "The University of Central Florida is a public research university with its main campus in unincorporated Orange County, Florida. UCF also has nine smaller regional campuses throughout central Florida. It is part of the State University System of Florida.", 61111);
INSERT INTO University (UniversityId, Address, Description, NumberOfStudents) VALUES ('b1c905da-d477-49fe-af2f-1caf1e7544ce', "201 Criser Hall PO Box 114000 Gainesville, FL 32611", "The University of Florida is a public land-grant research university in Gainesville, Florida. It is a senior member of the State University System of Florida, traces its origins to 1853, and has operated continuously on its Gainesville campus since September 1906.",  34881);
INSERT INTO University (UniversityId, Address, Description, NumberOfStudents) VALUES ('8cf421e8-4350-405e-8055-f0b7384c92e6', "SSB - Student Services Building, 1800 S Kirkman Rd, Orlando, FL 32811", "Valencia College is a public college in Orlando, Florida. It is part of the Florida College System. The college was founded in 1967 as Valencia Junior College and changed its name in 2010 because the academic scope of the school had expanded to include bachelor's degrees.", 36915);
